import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import './Login.css'; // Import the CSS file

function Login({ onLogin }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  // Updated port to 8080
  const API_BASE_URL = "http://localhost:8080";

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");
    setIsLoading(true);

    try {
      console.log('Attempting login with:', { email }); // Log login attempt

      const response = await fetch(`${API_BASE_URL}/api/admin/login`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          email: email,
          password: password,
        }),
      });

      console.log('Response received:', response.status); // Log response status

      if (!response.ok) {
        if (response.status === 401) {
          throw new Error('Invalid email or password');
        } else if (response.status === 404) {
          throw new Error('Server endpoint not found');
        } else {
          throw new Error(`Server responded with status: ${response.status}`);
        }
      }

      const data = await response.json();
      console.log('Response data:', data); // Log response data

      // Store token and user data
      if (data.token) {
        window.localStorage.setItem("jwtToken", data.token);
        const user = {
          email: email,
          token: data.token,
        };
        window.localStorage.setItem("loggedInUser", JSON.stringify(user));
        onLogin(user);
        navigate("/register");
      } else {
        throw new Error('No token received from server');
      }

    } catch (err) {
      console.error("Login error:", err);
      if (err.message === 'Failed to fetch') {
        setError(`Unable to connect to server at ${API_BASE_URL}. Please ensure the server is running and try again.`);
      } else {
        setError(err.message || 'Login failed. Please try again.');
      }
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="login-container">
      <h1 className="login-title">Admin Login</h1>

      <form onSubmit={handleLogin} className="login-form">
        <div className="form-group">
          <label className="form-label">
            Email:
          </label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="form-input"
            disabled={isLoading}
            required
            placeholder="Enter your email"
          />
        </div>

        <div className="form-group">
          <label className="form-label">
            Password:
          </label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="form-input"
            disabled={isLoading}
            required
            placeholder="Enter your password"
          />
        </div>

        <button
          type="submit"
          disabled={isLoading}
          className="login-button"
        >
          {isLoading ? "Logging in..." : "Login"}
        </button>
      </form>

      {error && (
        <div className="error-message">
          {error}
        </div>
      )}
    </div>
  );
}

export default Login;
